function setKeys(n)
  for i, key in pairs(keys) do
    tfm.exec.bindKeyboard(n, key, true, true)
    if settings.downKeys[key] then tfm.exec.bindKeyboard(n, key, false, true) end
  end
  -- Binding number keys 1-9.
  for key = 48, 57, 1 do
    tfm.exec.bindKeyboard(n, key, true, true)
  end
end

