function nextRound()
    tfm.exec.newGame(mapas[math.random(#mapas)]);
end
 
function eventNewGame()
    vivo=0
    tfm.exec.setUIMapName("Fishing 2012");
    for name,player in pairs(tfm.get.room.playerList) do
        vivo=vivo+1
    end
end
 
function eventPlayerDied(name)
    vivo=vivo-1
end
 
function eventPlayerWon(name)
    vivo=vivo-1
end
----Loop---
function eventLoop(ct, tr)
    if vivo == 0 or tr < 1000 then
        nextRound();
        pAtOnce=0
        tfm.exec.snow((0),(15))
        tfm.exec.disableAutoShaman(false)
    end

 for i=1,pAtOnce do
 tfm.exec.displayParticle(lights[math.random(#lights)],math.random(0,4800),math.random(0,400),0,0,0,0,nil)
 end
for i in pairs(tfm.get.room.playerList) do
if data[i].ban == true then
tfm.exec.killPlayer(i)
end
if data[i].wrp ==  true then
watch(data[i].vrp,i)
end
end
--Cor/color--
	for All in pairs(tfm.get.room.playerList) do
		for _,vAdmins in pairs(admins) do
			if(All == vAdmins) then --Se for admin...
				tfm.exec.setNameColor(All, 0xEB1D51) --Troque FFFFFF pela cor que deseja.
			end
		end
	end
end
nextRound();
--end loop and rotating maps--
function isAdm(p)
	for i,v in pairs(admins) do
  if v == p then
   return true
  end
 end
end

--comandos--
function eventChatCommand(player,c)
if c:sub(0,3) == [[rep]] then
for i,v in pairs(tfm.get.room.playerList) do
if i == c:sub(5) then
data[player].prp = ""..c:sub(5).."";
ui.addPopup(0,2,"Why are you reporting the player "..c:sub(5).."?",player,300,175,217,true)
end
end
end

if c == [[woff]] and isAdm(player) == true then
   data[player].wrp = false;
end

if c:sub(0,3) == [[par]]  and (c:sub(5) <= [[500]])  and isAdm(player) == true then
pAtOnce=c:sub(5)
print("<v>[Tigrounette] <n>Caution to the winds <j>".. player .."<n> Ò.Ó")
end

if c:sub(0,5) == [[fish]] and isAdm(player) == true then
pAtOnce=100
tfm.exec.disableAutoShaman(true)
tfm.exec.newGame("@4827484")
tfm.exec.setUIMapName("Fishing")
tfm.exec.giveMeep(player)
print("<v>[Tigrounette] <n>Welcome in my map <j>".. player .."<n>.")
end

if c:sub(0,4) == [[snow]] and isAdm(player) == true then
tfm.exec.snow((120),(15))
print("<v>[Tigrounette] <n>Ahhh, it is cold, <j>".. player .."<n> activate the snow!")
end

if (c == [[mort]]) then
	tfm.exec.killPlayer(player)
  print("<v>[Tigrounette] <j>".. player .." <n>dead because type the command <rose>"..c.."")
end

if c:sub(0,4) == [[stop]] and isAdm(player) == true then
system.exit()
end

if c:sub(0,4) == [[auto]] and isAdm(player) == true then
tfm.exec.setGameTime(120, true)
end

if c:sub(0,4) == [[next]] and isAdm(player) == true then
tfm.exec.newGame(mapas[math.random(#mapas)]);
tfm.exec.snow((0),(15))
pAtOnce=0
print("<v>[Tigrounette] <j>".. player .." <n>jumped the map!")
end

if c:sub(0,4) == [[help]] then
ui.addPopup(0, 0, "<font color='#009D9D'>"..player.."</font> <n>welcome, commands of players are:<br><br><font color='#BABD2F'>!mort</font> - Kill the player who executed the command.<br><font color='#BABD2F'>!rep NAME</font> - report for Admins.<br><br>Do you are <font color='#EB1D51'>administrator</font> type <font color='#BABD2F'>!helpadm</font>.", p, 250, 110, 250)
end

if c:sub(0,7) == [[helpadm]] and isAdm(player) == true then
ui.addPopup(0, 0, "<font color='#EB1D51'>"..player.."</font> welcome, commands of admins are:<br><br><font color='#BABD2F'>!fish</font> - Start map of fishing.<br><font color='#BABD2F'>!snow</font> - Is snowing.<br><font color='#BABD2F'>!par number</font> - Start particles.<br><font color='#BABD2F'>!next</font> - Next map in table mapas.<br><font color='#BABD2F'>!life</font> - reborn the admin.<br><font color='#BABD2F'>!np</font> - Choose a map.<br><font color='#BABD2F'>!win</font> - Player win<br><font color='#BABD2F'>!time</font> - Change to the time map.<br><font color='#BABD2F'>!woff</font> - Disable watch(beta)<br><font color='#BABD2F'>!kill</font> - Kill certain player.<br><font color='#BABD2F'>!stop</font> - Stop script.", p, 250, 110, 250)
end

if c:sub(0,4) == [[life]] and isAdm(player) == true then
tfm.exec.setGameTime(120, true)
tfm.exec.respawnPlayer(player)
vivo=vivo+1
end

if c:sub(0,4) == [[time]] and isAdm(player) == true then
                 ui.addPopup(9,2,"Time remaining:", player,300,100,200)
end

if c:sub(0,3) == [[np]] and isAdm(player) == true then
                 ui.addPopup(8,2,"Next map:", player,300,100,200)
end
if c:sub(0,3) == [[win]] and isAdm(player) == true then
                 ui.addPopup(7,2,"Win player?:", player,300,100,200)
end
if c:sub(0,4) == [[kill]] and isAdm(player) == true then
                 ui.addPopup(6,2,"Kill player:", player,300,100,200)
end
end

function eventPopupAnswer(id, player, answer)
if id == 9 then
tfm.exec.setGameTime(answer)
end

if id == 8 then
tfm.exec.newGame(answer)
end
if id == 7 then
tfm.exec.giveCheese(answer)
tfm.exec.playerVictory(answer)
end
if id == 6 then
tfm.exec.killPlayer(answer)
end

if id == 0 then
if data[data[player].prp].nrp <= 20 then
data[player].mrp = ""..answer.."";
ui.addPopup(0,0,"<p align='center'>Your demand has been taken into consideration.</p>",player,300,175,200,true)
repPlayer(data[player].prp,answer,player)
sc.urp = "<R>"..player.."<N2> reported <VP>"..data[player].prp.."";
else
ui.addPopup(0,0,"<p align='center'>The player already has many reports.</p>",player,300,175,200,true)
end
end
if id == 1 then
for i,v in pairs(tfm.get.room.playerList) do
if i == answer then
data[player].vrp = ""..answer.."";
ui.updateTextArea(1,"<p align='center'><VP><TI><b>M  O  D  O   P  W  E  T</b></p><br /><V><a href='event:close'>Close</a><br /><font size='12'><J>"..answer.."<BL> "..data[answer].nrp.." - [<R><a href='event:brp'>ban</a></a><BL>] - [<R><a href='event:wrp'>watch</a></a><BL>] - [<R><a href='event:arp'>alert</a></a><BL>]"..data[answer].rps.."",player)
end
end
end
if id == 2 then
for i,v in pairs(tfm.get.room.playerList) do
if i == answer then
data[answer].ban = true;
ui.addPopup(0,0,"<p align='center'>The player "..answer.." was banned!",player,300,175,200,true)
tfm.exec.killPlayer(answer)
end
end
end
if id == 3 then
for i,v in pairs(tfm.get.room.playerList) do
if i == answer then
data[answer].ban = false;
ui.addPopup(0,0,"<p align='center'>The player "..answer.." was unbanned!",player,300,175,200,true)
end
end
end
if id == 4 then
for i,v in pairs(tfm.get.room.playerList) do
if i == answer then
data[player].ale = ""..answer.."";
ui.addPopup(5,2,"<p align='center'>What message?",player,300,175,200,true)
end
end
end
if id == 5 then
ui.addPopup(0,0,"<p align='center'><font color='#ED67EA'>[~"..player.."]</font> "..answer.."",data[player].ale,300,175,200,true)
end
end
--end commandos--
-- Atualizar reports de um jogador
function repPlayer(player,msg,r)
data[player].rps = data[player].rps.."<br /><N2>[<V>"..player.."<N2>]<BL> "..r.." : <N>"..msg.."";
data[player].nrp = data[player].nrp+1;
sc.rps=sc.rps+1;
end
-- Links de uma textarea
function eventTextAreaCallback(id,player,link)
if link == [[m]] then
ui.addTextArea(1,"<p align='center'><VP><TI><b>M  O  D  O   P  W  E  T</b><br /></p><J>• Statistics of reports:<br /><font size='12'><V>Number of people reported : <N2>"..sc.rps.."<br /><V>Last report : <N2>"..sc.urp.."<br /><br /><TI><J>• Tools:<br /><V><font size='12'><a href='event:r'>See reports of a player.</a><br /><a href='event:ban'>Ban a player.</a><br /><a href='event:unb'>Unban a player.</a><br /><a href='event:ale'>Warning a player.</a><br /><a href='event:close'>Close.</a>",player,20,60,700,260,1,1,0.8,true)
ui.addTextArea(0,"<V><a href='event:m'>Modo Pwet</a>",v,15,25,70,20,1,1,0.8,true)
end
if link == [[close]] then
ui.removeTextArea(id,player)
end
if link == [[r]] then
ui.addPopup(1,2,"Which player?",player,300,175,200,true)
end
if link == [[ban]] then
ui.addPopup(2,2,"Which player?",player,300,175,200,true)
end
if link == [[unb]] then
ui.addPopup(3,2,"Which player?",player,300,175,200,true)
end
if link == [[ale]] then
ui.addPopup(4,2,"Which player?",player,300,175,200,true)
end
if link == [[brp]] then
data[data[player].vrp].ban = true;
ui.addPopup(0,0,"<p align='center'>The player "..data[player].vrp.." was banned!",player,300,175,200,true)
end
if link == [[wrp]] then
data[player].wrp = true;
end
if link == [[arp]] then
data[player].ale = ""..data[player].vrp.."";
ui.addPopup(5,2,"<p align='center'>What message?",player,300,175,200,true)
end
end
